import React from 'react';

class FileItem extends React.Component {
    render() {
        return (
            <div style={{position: 'relative'}}>
                <p style={itemStyle}>
                    {this.props.file}
                    <button style={removeStyle}>Remove</button>
                    <button style={downloadStyle}>Download</button>
                </p>
            </div>
        );
    }
}

const itemStyle = {
    fontSize: '2.2em',
    margin: '1em 0.5em',
    padding: '1em 1.2em',
    border: '3px solid black',
    borderRadius: '8px',
    background: '#EAEAEA'
};

const downloadStyle = {
    fontSize: '1.2em',
    margin: '0 2em',
    float: 'right',
    border: 'none',
    background: 'green'
};

const removeStyle = {
    fontSize: '1.2em',
    margin: '0 2em',
    float: 'right',
    color: 'red'
};

export default FileItem;
