import React from 'react';

class About extends React.Component {
    render() {
        return (
            <div>
                <h1 style={headingStyle}>About</h1>
                <div style={aboutDivStyle}>
                    <h3 style={aboutStyle}>Codex.io allows you to</h3>
                    <p style={aboutStyle2}><span style={{fontSize:'3rem', fontStyle: 'italic'}}>Upload</span> and <span style={{fontSize:'3rem', fontStyle: 'italic'}}>download</span> files for different classrooms for easy access</p>
                </div>
            </div>
        );
    }
}

const headingStyle = {
    textAlign: 'center',
    backgroundColor: "black",
    color: '#FFFBFF',
    padding: '0.8em'
};

const aboutStyle = {
    padding: '0.5em',
    fontSize: '2.5em'
};

const aboutStyle2 = {
    padding: '0 0.5em',
    fontSize: '2em'
};

const aboutDivStyle = {
    textAlign: 'center'
};

export default About;
