    render() {
        return (
            <div>
                <form onSubmit={this.props.handleSubmit}>
                    <input type="file" name="upload" id="upload" ref={this.props.fileInput} />
                    <input type="submit" value="Upload"/>
                </form>
            </div>
        );
    }
}

