import React from 'react';

class FileItem extends React.Component {
    render() {
        return (
            <div style={{position: 'fixed'}}>
                <p style={itemStyle}>
                    {this.props.file}
                    <button style={downloadStyle}>Download</button>
                    <button>Remove</button>
                </p>
            </div>
        );
    }
}

const itemStyle = {
    fontSize: '2.2em',
    margin: '0.5em',
    padding: '2em 0'
};

const downloadStyle = {
    fontSize: '2.2em',
    float: 'right'
};

export default FileItem;
